import 'package:easy_localization/easy_localization.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';class GCSureAlertDialog extends StatefulWidget{  String content = "";  String title = "";  ///0--LPR , 1--基点  int type = 0;  GCSureAlertDialog({required this.type}):super();  @override  State<StatefulWidget> createState() {    // TODO: implement createState    return GCSureAlertDialogState();  }}class GCSureAlertDialogState extends State<GCSureAlertDialog>{  @override  void initState() {    // TODO: implement initState    super.initState();    if(widget.type == 0){      widget.title = "LPR(貸款市場報價利率)";      widget.content = "自2019年10月起，商貸利率開始改用LPR(貸款市場報價利率)計算。LPR基準利率每月更新一次，實際貸款利率在LPR的基礎上進行一定的浮動。";    }else{      widget.title = "什麼是基點？";      widget.content = "1個基點=0.01%，如果浮動10個基點，相當於在LPR的基礎上增加0.01%為實際貸款利率。";    }  }  @override  Widget build(BuildContext context) {    // TODO: implement build    return Material(      color: Color(0x80000000),      child: Center(        child: Container(          decoration: BoxDecoration(            color: Colors.white,            borderRadius: BorderRadius.all(Radius.circular(10)),            shape: BoxShape.rectangle          ),          margin: EdgeInsets.only(left: 38,right: 38),          child: Column(            mainAxisSize: MainAxisSize.min,            children: [              Container(                margin: EdgeInsets.only(top: 20),                child: Text(                  widget.title,                  style: TextStyle(                      fontSize: 16,                      color: Color(0xFF333333),                      fontWeight: FontWeight.w600                  ),                ),              ),              Container(                margin: EdgeInsets.only(left: 20,right: 20,top: 20),                child: Text(                  widget.content,                  style: TextStyle(                      color: Color(0xFF333333),                      fontSize: 12                  ),                  softWrap: true,                ),              ),              GestureDetector(                onTap: (){                  Navigator.of(context).pop();                },                child: Container(                  margin: EdgeInsets.only(left: 20,right: 20,top: 20,bottom: 20),                  height: 40,                  decoration: BoxDecoration(                      shape: BoxShape.rectangle,                      borderRadius: BorderRadius.all(Radius.circular(4)),                      border: Border.all(color: Color(0xFFB03725),width: 1)                  ),                  alignment: Alignment.center,                  child: Text(                    tr("我知道了"),                    style: TextStyle(                        fontSize: 16,                        color: Color(0xFFB03725)                    ),                  ),                ),              )            ],          ),        ),      ),    );  }}