import 'package:easy_localization/easy_localization.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'Wights/GCNotifyCellWidget.dart';class GCNotifyListPageView extends StatefulWidget{  List? notifyList;  bool showEdit = false;  @override  State<StatefulWidget> createState() {    // TODO: implement createState    return GCNotifyListState();  }  }class GCNotifyListState extends State<GCNotifyListPageView>{  @override  Widget build(BuildContext context) {    // TODO: implement build    return Scaffold(      appBar: PreferredSize(          preferredSize: Size(MediaQuery.of(context).size.width,45),          child:Container(            child: AppBar(                backgroundColor: Colors.transparent,                elevation: 0,                centerTitle: true,                title: Text(tr("消息通知"),                  style: TextStyle(fontSize: 18,),                ),                leading: IconButton(                  icon: Icon(Icons.arrow_back_ios_outlined),                  onPressed: (){                    Navigator.of(context).pop();                  },                ),                actions: [                  TextButton(onPressed: (){                    setState(() {                      widget.showEdit = widget.showEdit == true?false:true;                    });                  }, child: Text(                    tr("管理"),                    style: TextStyle(                        color: Colors.white,                        fontSize: 14                    ),                  )                  )                ]            ),            decoration: BoxDecoration(                image: DecorationImage(                    image: ExactAssetImage("assets/iOSNavBar/daohanglan.png"),                    fit: BoxFit.cover                )            ),          )),      body: ListView.builder(        itemCount: 3,        itemBuilder: ( context , index){          // final item = GCNotifyCellWidget();          return GCNotifyCellWidget(showEdit: widget.showEdit,showSelectIcon: true,deleteCallBack: (){            widget.notifyList!.removeAt(index);          },);        },      ),    );  }  }