import 'dart:developer';import 'package:easy_localization/easy_localization.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';// ignore: must_be_immutableclass GCFiveTopModel extends StatefulWidget{  List dataList ;  List<GCTopOneWidget> topModelArray = [] ;  GCFiveTopModel({required this.dataList}):super();  @override  State<StatefulWidget> createState() {    // TODO: implement createState   return GCFiveTopState();  }}class GCFiveTopState extends State<GCFiveTopModel>{  @override  void initState() {    // TODO: implement initState    changeState();    super.initState();  }  void changeState(){      setState(() {        if(widget.dataList != null){          widget.topModelArray.clear();        }        for (var value in widget.dataList) {             widget.topModelArray.add(GCTopOneWidget(title: value["title"],imageUrl: value["imageUrl"],));        }      });  }  @override  Widget build(BuildContext context) {    // TODO: implement build    return Container(      padding: EdgeInsets.only(left: 15,right: 15),        decoration: BoxDecoration(          image: DecorationImage(            image: AssetImage("assets/homepage/backgroundImage/background@3x.png"),            fit: BoxFit.cover          )        ),        height: 80,        child: Row(          mainAxisAlignment: MainAxisAlignment.spaceBetween,          mainAxisSize: MainAxisSize.max,          crossAxisAlignment: CrossAxisAlignment.center,          children: widget.topModelArray,        ));  }}class GCTopOneWidget extends StatelessWidget{  String title;  String imageUrl;  GCTopOneWidget({required this.title,required this.imageUrl}):super();  @override  Widget build(BuildContext context) {    // TODO: implement build    return Column(      children: [          Container(            margin: EdgeInsets.only(top: 14),            height: 26,            width: 26,            alignment: Alignment.center,            child: Image(              image: AssetImage(this.imageUrl),            ),          ),          Container(            margin: EdgeInsets.only(top: 9),            child: Text(              tr(title),              style: TextStyle(                fontSize: 12,                color: Colors.white              ),            ),          )      ],    );  }}