import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';class GCRowWidget extends StatelessWidget{  VoidCallback onTap;  String iconUrl;  String title;  String rightIcon;  GCRowWidget({required this.iconUrl,required this.title,required this.rightIcon,required this.onTap}):super();  @override  Widget build(BuildContext context) {    // TODO: implement build    return GestureDetector(      child: Container(        height: 54,        child: Row(          children: [            Container(              alignment: Alignment.center,              margin: EdgeInsets.only(left: 15),              height: 24,              width: 24,              decoration: BoxDecoration(                  image: DecorationImage(                      image: AssetImage(this.iconUrl),                      fit: BoxFit.cover                  )              ),            ),            Expanded(child: Container(              height: 54,              margin: EdgeInsets.only(left: 15),              alignment: Alignment.centerLeft,              child: Text(                this.title,                style: TextStyle(                    color: Color(0xFF333333),                    fontSize: 16,                    fontWeight: FontWeight.w500                ),                textAlign: TextAlign.left,              ),            )),            Container(              margin: EdgeInsets.only(right: 15),              height: 16,              width: 16,              decoration: BoxDecoration(                  image: DecorationImage(                      image: AssetImage(this.rightIcon),                      fit: BoxFit.cover                  )              ),            )          ],        ),      ),      onTap: this.onTap,    );  }}