import 'package:easy_localization/easy_localization.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';class GCButton extends StatelessWidget{  GCButton({    Key? key,    required this.title,    required this.marginEdg,    required this.onPressed  }):super(key: key);  String title;  EdgeInsets marginEdg;  VoidCallback onPressed;  @override  Widget build(BuildContext context) {    // TODO: implement build    var text = tr(title);    return GestureDetector(      onTap: (){        if(onPressed!=null) {          onPressed();        }      },      child: Container(        width: 400,        height: 40,        margin: marginEdg,        alignment: Alignment.center,        decoration: BoxDecoration(            shape: BoxShape.rectangle,            border: Border.all(color:Color(0xFFB03725),width: 1 ),            borderRadius: BorderRadius.all(Radius.circular(4.0))        ),        padding: EdgeInsets.all(1),        child: new Text(          text,          style: TextStyle(              color: Color(0xFFB03725),              fontSize: 16          ),          textAlign: TextAlign.center,        ),      ),    );  }}