import 'package:easy_localization/easy_localization.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';// ignore: must_be_immutableclass GCNavBar extends StatelessWidget implements PreferredSizeWidget{  String title;  Color textColor = Colors.white;  Icon leftIcon;  Widget? rightIcon1;  Widget? rightIcon2;  VoidCallback onPressed1 = (){};  VoidCallback onPressed2 = (){};  GCNavBar({required this.title,required this.leftIcon,rightIcon1,rightIcon2,onPressed1,onPressed2,Key? key});  @override  Widget build(BuildContext context) {    // TODO: implement build    return PreferredSize(        preferredSize: Size(MediaQuery.of(context).size.width,45),        child:Container(          child: AppBar(            backgroundColor: Colors.transparent,            elevation: 0,            centerTitle: true,            title: Text(tr(this.title),              style: TextStyle(fontSize: 18,),            ),            leading: IconButton(              icon: leftIcon,              onPressed: (){                Navigator.of(context).pop();              },            ),            // ignore: unnecessary_null_comparison            actions: this.rightIcon2==null?(this.rightIcon1==null?[]: [              IconButton(icon: this.rightIcon1!, onPressed: this.onPressed1)            ]):[              IconButton(icon: this.rightIcon1!, onPressed: this.onPressed1),              IconButton(icon: this.rightIcon2!, onPressed: this.onPressed2)            ],          ),          decoration: BoxDecoration(            image: DecorationImage(              image: ExactAssetImage("assets/iOSNavBar/daohanglan.png"),              fit: BoxFit.cover            )          ),        ));  }  @override  // TODO: implement preferredSize  Size get preferredSize =>  new Size.fromHeight(45);}